let initialState = {
  courseList: [],
  // selectedCourse:null,
  // courseCourse:null,
};

const reducer = (state = initialState, { type, payload }) => {
  switch (type) {
      case "SET_COURSE": {
          state.courseList = payload;
      }
    default:
      return state;
  }
};

export default reducer;
