import { request } from "../../configs/request";

import { creatorAction } from "./index";
import { SET_COURSES } from "./type";

export const signIn = (data) => {
  return (dispatch) => {
    request(
      "http://elearning0706.cybersoft.edu.vn/api/QuanLyNguoiDung/DangNhap",
      "POST",
      data
    )
      .then((res) => {
        console.log(res);
        localStorage.setItem("t", res.data.accessToken);
        dispatch(creatorAction(SET_COURSES, res.data.accessToken));
      })
      .catch((err) => {
        console.log(err);
      });
  };
};
