import { createMuiTheme } from "@material-ui/core/styles";


const theme = createMuiTheme({
    palette:{
        primary:{
            light:"rgba(255,0,0,0.5)",
            main:"rgba(255,0,0)",
            dark:"rgba(255,0,0,0.1)"
        },
        secondary:{
            light:"rgba(0,255,0,0.5)",
            main:"rgba(0,255,0)",
            dark:"rgba(0,255,0,0.1)"
        },
        background
    },
    typography:{
        h1:{
            fontSize: 90,
            fontWeight:700,
        },
    },
    spacing: 8,
})
export default theme;
