export const style = (theme) => {
  return {
    heightImg: {
      height: 200,
    },
    btn:{
      background: "#fafafa",
      color: theme.palette.text.primary,
      "&:hover":{
        backgroundColor: theme.palette.secondary.light,
      },
      "& span":{

      },
      //responsive
      [theme.breakpoints.down("sm")]: {
        color: theme.palette.text.primary,
      }
    }

  };

};
