import { SET_COURSES } from "../actions/type";

let initialState = {
  courseList: [],
  // selectedCourse:null,
  // courseCourse:null,
};

const reducer = (state = initialState, { type, payload }) => {
  switch (type) {
      case SET_COURSES: {
          const cloneCourseList = [...state]
          for(let item of payload){
              state.courseList.push(item);
          }
          return {...state}
      }
    default:
      return state;
  }
};

export default reducer;
