import React, { Component } from "react";
import {
  CardActionArea,
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
  withStyles,
} from "@material-ui/core";
import { style } from "./style";
class CardItem extends Component {
  
  render() {
    console.log();
    const{maKhoaHoc,hinhAnh, moTa} = this.props.item;
    return (
      <div>
        <Card>
          <CardActionArea>
            <CardMedia
              style={{ height: 200 }}
              image={hinhAnh}
              title="Contemplative Reptile"
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="h2">
               {maKhoaHoc}
              </Typography>
              <Typography variant="body2" color="textSecondary" component="p">
                {moTa}
              </Typography>
            </CardContent>
          </CardActionArea>
          <CardActions>
            <Button variant="outlined" color="secondary">
              View Detail
            </Button>
          </CardActions>
        </Card>
      </div>
    );
  }
}
export default withStyles(style)(CardItem);
